// Header.jsx

import React from 'react';
import { Container, Row, Button } from 'reactstrap';
import { Link } from 'react-router-dom';
import logo from '../../assets/images/logo.png';
import "./header.css";

const nav__links = [
  { path: '/home', display: 'Home' },
  { path: '/about', display: 'About' },
  { path: '/tours', display: 'Tours' }
];

const Header = () => {
  const scrollToPlaces = () => {
    const element = document.getElementById("places");
    if (element) {
      element.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <header className="header">
      <Container>
        <Row>
          <div className="nav__wrapper d-flex align-items-center justify-content-between">
            {/* Logo */}
            <div className="logo">
              <img src={logo} alt="" />
            </div>
            {/* Navigation */}
            <div className="navigation">
              <ul className="menu d-flex align-items-center gap-5">
                {nav__links.map((item, index) => (
                  <li className="nav__item" key={index}>
                    {item.path === '/tours' ? (
                      <Link to="#" onClick={scrollToPlaces} className="nav-link">
                        {item.display}
                      </Link>
                    ) : (
                      <Link to={item.path} className="nav-link">
                        {item.display}
                      </Link>
                    )}
                  </li>
                ))}
              </ul>
            </div>
            {/* Navigation buttons */}
            <div className="nav__right d-flex algin-item-center gap-4">
              <div className="nav__btns  d-flex algin-item-center gap-4">
                <Button className="btn secondary__btn"><Link to='/login'>Login</Link></Button>
                <Button className="btn primary__btn"><Link to='/register'>Register</Link></Button>
              </div>
              <span className="mobile">
                <i className="ri-menu-add-fill"></i>
              </span>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;



